    }

    return (
        <form>
            <label> Your message </label>
            <input type="text" onChange={handleOnChange} value={enteredValue}/>
            <p>{enteredValue.trim().length < 3 ?  "Invalid" : "Valid"} message</p>
        </form>
    );
}
